import { USER_INPUT } from "./actions";

const initialState = {
  bills: {
    rent: { amount: null, paidBy: "Katherine" },
    electric: { amount: 0, paidBy: "Conner" },
    renters: { amount: 0, paidBy: "Katherine" }
  },
  people: {
    katherine: {
      name: "Katherine",
      amountPaid: 0,
      paidMore: false
    },
    conner: {
      name: "Conner",
      amountPaid: 0,
      paidMore: false
    }
  }
};

function reducer(state = initialState, action) {
  switch (action.type) {
    case USER_INPUT:
      console.log(
        Object.assign({}, state.bills.rent, {
          amount: action.payload.rent
        })
      );
    default:
      return state;
  }
}

export default reducer;
