import { USER_INPUT } from "./actions";
import { Map } from "immutable";

const initialState = Map({
  bills: Map({
    rent: { amount: null, paidBy: "Katherine" },
    electric: { amount: 0, paidBy: "Conner" },
    renters: { amount: 0, paidBy: "Katherine" }
  }),
  people: Map({
    katherine: Map({
      name: "Katherine",
      amountPaid: 0,
      paidMore: false
    }),
    conner: Map({
      name: "Conner",
      amountPaid: 0,
      paidMore: false
    })
  })
});

function reducer(state = initialState, action) {
  switch (action.type) {
    case USER_INPUT:
      return state.setIn(
        ["bills", `${Object.keys(action.payload)[0]}`, "amount"],
        action.payload
      );
    default:
      return state;
  }
}

export default reducer;
